mapper.select:
select * from jianli_tb,role_tb where jianli_tb.job_id = role_tb.role_id and jianli_tb.state not in (0)
and xingming like "%"#{xingming}"%"
and role_name like "%"#{roleBean.roleName}"%"
and jianyan like "%"#{jianyan}"%"
and state = #{state}
and dtime > #{dtime}

select * from jianli_tb,role_tb where jianli_tb.job_id = role_tb.role_id and jianli_tb.state not in (0,4,5,6)
and xingming like "%"#{xingming}"%"
and role_name like "%"#{roleBean.roleName}"%"
and jianyan like "%"#{jianyan}"%"
and state = #{state}
and dtime > #{dtime}

select * from jianli_tb,role_tb where jianli_tb.job_id = role_tb.role_id and jianli_tb.state not in (0,1,2,3,6)
and xingming like "%"#{xingming}"%"
and role_name like "%"#{roleBean.roleName}"%"
and jianyan like "%"#{jianyan}"%"
and state = #{state}
and dtime > #{dtime}

mapper.update:
update jianli_tb
set 
  state= #{0}
where jianli_id = #{1}
